                  $t.html(c);



                }

                //transact all data
                if(e.feed.entry){
                  for(var k =0;k < e.feed.link.length;k++){
                    if(e.feed.link[k].rel == "alternate"){
                      var lu = e.feed.link[k].href + '?max-results=' + perpage;
