                  var postcount = e.feed.openSearch$itemsPerPage.$t;
                  var startindex = e.feed.openSearch$startIndex.$t;
                  var levels = Math.ceil(totalpost/postcount);						

                  for(var i=0;i<e.feed.entry.length;i++){

                    //grab details
